import React from 'react';

import './ChatTitle.css';

const ChatTitle = ({ conversations, selectedConversation }) => {

    const convHeader = conversations.map((conversation) => {
        return <div id="chat-title">
             <img src={conversations.imageUrl} alt={conversations.imageAlt} />
            key={conversation.id}
            <span>{selectedConversation.title}</span>
            </div>
    });
    return (
        {convHeader}
    );
}

export default ChatTitle;
