import React from 'react';
import ConversationSearch from "../../components/conversation/conversation-search";
import ConversationList from '../../components/conversation/conversation-list/ConversationList';
import NewConversation from '../../components/conversation/new-conversation/NewConversation';
import ChatTitle from '../../components/chat-title/ChatTitle';
import MessageList from '../../components/message/MessageList';
import ChatForm from '../../components/chat-form/Chat-Form';
import { conversations, selectedConversation } from '../../chat/data/conversations';
import { messages } from '../../chat/data/messages';

import './ChatShell.css';

const ChatShell = ({
    conversations,
    selectedConversation,
    conversationChanged
}) => {

    return (
        <div id="chat-container">
        <ConversationSearch />
        <ConversationList
            onConversationItemSelected={conversationChanged}
            conversations={conversations}
            selectedConversationId={selectedConversation.id} />
        <NewConversation />
        <ChatTitle selectedConversation={selectedConversation} />
        <MessageList messages={selectedConversation.messages} />
        <ChatForm />
    </div>
    );
}

export default ChatShell;
