import React from 'react';

import "./Message.css"

const Message = (props) => {
    let messageClass = "message-row",
        imageThumbNail = null;

    if(props.isMyMessage) {
        messageClass += " you-message";
    } else {
        messageClass += " other-message";
        imageThumbNail = <img src={props.message.imageUrl} alt={props.message.imageAlt} />

    }

    return (
        <div className={messageClass}>
                <div className="message-content">
                    {imageThumbNail}
                    <div className="message-text">
                       {props.message.messageText}
                    </div>
                    <div className="message-time">{props.message.createdAt}</div>
                </div>
            </div>
    )
}

export default Message;

// Yeah I think it's best we do that. Otherwise things won't work well at all. 
// I'm adding more text here to test the sizing of the speech bubble and the 
// wrapping of it too.

